import React, { Component } from 'react';
import './App.css';
import Home from './components/pages/Home/Home';

class App extends Component {
  state = { movies: null, error: '', isLoading: true };
  updateMovies(movies) {
    this.setState({ movies });
  }

  componentDidMount() {
    // const response = await fetch(`https://academy-video-api.herokuapp.com/content/free-items`, {
    //   method: 'GET',
    //   headers: {
    //     'Content-Type': 'application/json',
    //   },
    // });
    // this.movies = await response.json();
    // this.updateMovies(this.movies);
    // console.log('Movies:', this.state.movies);

    fetch(`https://academy-video-api.herokuapp.com/content/free-items`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    })
      .then((response) => {
        if (!response.ok) throw new Error(response.status);
        else return response.json();
      })
      .then((data) => {
        this.setState({ isLoading: false, movies: data.response });
        console.log('Data:', data.response);
      })
      .catch((error) => {
        console.log('Error: ' + error);
        this.setState({ error: error });
      });
  }
  render() {
    return (
      <div className="App">
        <Home error={this.state.error} movies={this.movies} loading={this.state.isLoading} />
      </div>
    );
  }
}
export default App;
