import Card from '../Card/Card';
import './Movies.css';

const Movies = ({ movies, error, loading, children }) => {
  return (
    <div className="section">
      <div className="container movies-container">
        <div className="columns is-multiline">
          {loading && !error && <h3 className="has-text-white has-text-centered">Loading movies...</h3>}
          {error && <h3 className="has-text-danger has-text-centered">{error}</h3>}
          {!!movies &&
            movies.map((movie) => {
              return (
                <Card
                  key={movie.id}
                  title={movie.title}
                  description={movie.description.substring(0, 55)}
                  image={movie.image}
                  movieId={movie.id}
                />
              );
            })}
          {error && <h3 className="has-text-white has-text-centered">{error}</h3>}
        </div>
        {children}
      </div>
    </div>
  );
};

export default Movies;
