import Card from '../../molecules/Card/Card';
import './Movies.css';

const Movies = ({ movies, error, loading, children }) => {
  console.log('Movies component data:', movies);
  return (
    <div className="section">
      <div className="movies">
        {loading && !error && <h3 className="movies__loading">Loading movies...</h3>}
        {error && <h3 className="movies__error">{error}</h3>}
        {!!movies &&
          movies.map((movie) => {
            return (
              <Card
                key={movie.id}
                title={movie.title}
                description={movie.description.substring(0, 55)}
                image={movie.image}
                movieId={movie.id}
              />
            );
          })}
      </div>
      {children}
    </div>
  );
};

export default Movies;
