    console.log('favFunction ID:', movieId);
    setisFavorite(!isFavorite);
    console.log(event);
    event.target.classList.toggle('favorited');
  };

  return (
    <button className="favorite-button" onClick={favFunction}>
      {isFavorite ? 'Remove ðŸ’”' : 'Favorite'}
    </button>
  );
};

