import Button from '../../atoms/Button/Button';
import Card from '../../molecules/Card/Card';
import './Movies.css';

const Movies = ({ movies, error, loading }) => {
  console.log('Movies component data:', movies);
  return (
    <section className="movies">
      {loading && !error && <h3 className="movies__loading">Loading movies...</h3>}
      {error && <h3 className="movies__error">{error}</h3>}
      {!!movies &&
        movies.map((movie) => {
          return (
            <Card
              key={movie.id}
              title={movie.title}
              description={movie.description.substring(0, 55)}
              image={movie.image}
              movieId={movie.id}
            />
          );
        })}
      <Button>Get More Content</Button>
    </section>
  );
};

export default Movies;
